import React from 'react';
import s from './Post.module.css';
import global from './global.js';



const PostText = (props) =>{
return <div className={s.untext}>{props.likeCount} </div>
}

const Message = (props) =>{
return <div className={s.untext}>{props.message} </div>

}


let newPostElement = React.createRef();
let addPost = () => {
    let text = newPostElement.current.value;
    alert(text);
}

const Post =(props) => { 


      let posts =  [
            {id: 1, likeCount: "first"},
            {id: 2, likeCount: "second"},
            {id: 3, likeCount: "fttrst"},
            {id: 4, likeCount: "sugcond"},
            {id: 5, likeCount: "uuirst"},
            {id: 6, likeCount: "seiggond"}                   ]

    let postElements = posts.map( p =>
     <PostText likeCount={p.likeCount} id={p.id} />);





    let messages = [
    {id: 1, message:'Hey'},
    {id: 2, message:'How are you?'},
    {id: 3, message:'Hey'},
    {id: 4, message:'How are you?'},
    {id: 5, message:'Hey'},
    {id: 6, message:'How are you?'},
    {id: 7, message:'Were you been, my friend'}  
    ]

    let messagesElements = messages.map( m => 
    <Message message={m.message} id={m.id} /> );



    return( <div className="post-item">
     <div className={s.item}> 

     <img className="img-post" src="https://i.ibb.co/Fzxbqzn/nastol-com-ua-138035.jpg" alt="post"/>
     <div> Like { postElements } </div>
     <div> fff {messagesElements} </div>
     <textarea ref={newPostElement}></textarea>
     <button onClick={ addPost }>Add post</button>

     </div>

    </div>);
}

export default Post;
